import { styled } from 'styled-components';

export const Main = styled.main`
    min-height: 70vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
`;

export const Article = styled.article`
    width: 50vw;
    margin: 30px auto;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    border: #fff solid 1px;
    border-radius: 10px;
    padding: 15px 10px;

    .linkJob{
        color: green;
        
    }

    .dataLink{
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        align-items: end;
        width: 40%;
    }

    .dataJob{
        width: 60%;
    }
`;
