import { styled } from 'styled-components';

export const Container = styled.main`
    min-height: 70vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    
    form{
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
    }
`;

export const Input = styled.input`
    padding: 1rem;
    width: 70vw;
    border: none;
    outline: none;
    border-top: solid #000 1px;
    margin-bottom: 1rem;
`;
