import { styled } from 'styled-components';

export const Main = styled.main`
    min-height: 70vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;

    #introJobs{
        font-size: 1.2rem;
        margin: 20px;
    }
`;

export const Article = styled.article`
    width: 60vw;
    background-color: #0e365e;
    margin: 30px auto;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    border-radius: 10px;
    padding: 15px 10px;

    &:hover{
        box-shadow: 5px 5px 5px black;
    }

    .jobTitle{
        margin-bottom: 15px;
        font-size: 1.3rem;
    }

    .companyName{
        color: #d5d9d4;
    }

    .linkJob{
        color: #65f24b;
        text-decoration: none;
        transition: 0.3;
    }

    .linkJob:hover{
        color: #2eb814;
    }

    .dataLink{
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        align-items: end;
        width: 40%;
    }

    .dataJob{
        width: 60%;
    }
`;
