import { useState } from "react";
import { Container, Input, Submit } from "./styles";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

export default function Main(){
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        jobname: '',
        jobcity: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/jobs', {
                jobname: formData.jobname,
                jobcity: formData.jobcity
            });
    
            if (response.status === 200) {
                navigate('/jobs');
            } else {
                console.error('Erro ao enviar formul치rio');
            }
        } catch (error) {
            console.error('Erro ao enviar formul치rio:', error);
        }
    };


    return(
        <Container>
            <form onSubmit={handleSubmit}>
                <Input 
                    type="text" 
                    name="jobname" 
                    id="jobname"
                    value={formData.jobname}
                    onChange={handleChange}
                    placeholder="Digite o Cargo ou Empresa" />
                    
                <Input 
                    type="text" 
                    name="jobcity" 
                    id="jobcity"
                    placeholder="Insira a Cidade"
                    value={formData.jobcity}
                    onChange={handleChange} />

                <Submit type="submit" value="Enviar" />
            </form>
        </Container>
    )
}
